function isNumberPrime(n) {
  "use strict";
  if (n < 2 || n % 1 || n % 2 === 0 || n % 3 === 0) {
    return false;
  }
  let root = Math.floor(Math.sqrt(n));
  for (let i = 5; i <= root; i += 6) {
    if (n % i === 0 || n % (i + 2) == 0) {
      return false;
    }
  }
  return true;
}

export default isNumberPrime;