/*global describe it*/
import assert from 'assert';
import isNumberPrime from '../src/isNumberPrime.js';

const primes = [53, 59, 61, 67, 71, 73, 79, 83, 89, 97];
const notPrimes = [1, 4, 6, 9, 10, 12, 14, 15, 16, 18];


describe('isNumberPrime()', function () {
  this.timeout(0)
  primes.forEach(function (n) {
    it('finds that ' + n + ' is a prime number.', function () {
      assert.equal(isNumberPrime(n), true);
    });
  });
});


describe('isNumberPrime()', function () {
  this.timeout(0)
  notPrimes.forEach(function (n) {
    it('finds that ' + n + ' is not a prime number.', function () {
      assert.equal(isNumberPrime(n), false);
    });
  });
});

